#Vectors
#Subsetting with negative indexes
v <- c(1,2,3)
v[-c(1,3)]
## [1] 2
#Subsetting with logical vectors
#Here the logical vector should be the same length, or to be divided exactly with the vector that we want to subset

v <- seq (1, 100, 2)
lv <- c(T,F)
v[lv]
##  [1]  1  5  9 13 17 21 25 29 33 37 41 45 49 53 57 61 65 69 73 77 81 85 89
## [24] 93 97
#Named vectors
v <- c(1,2,3)
names(v) <- c('a', 'b', 'c')

v[1]
## a 
