#Example with named vectors
#Constructing the complementary DNA strand
alphabet <- c('A', 'C', 'G', 'T')
names(alphabet) <- c('T', 'G', 'C', 'A')

dna <- sample(alphabet, 1000, TRUE)
compDNA <- alphabet[dna]





paste(dna, collapse='')


## [1] ""





paste(compDNA, collapse='')




## [1] ""
